// import layerOne from "../assets/slider/parallaximg/layerOne.jpeg";
function ProductName(props) {
  return (<><div className="text-start text-6xl leading-tight ">
      <h1>{props.title}</h1>
    </div>
    <div className="text-start py-5 ">
      <h3>{props.text}</h3>
    </div>
  </>

  );
}
// function SubHeadingProduct(props) {
//   return (

//   );
// }
function ConsistOf(props) {
  return (
    <div className="text-start">
      <h3>{props.heding}</h3>
      <div className="text-center px-14 py-2">
        <p>{props.discription}</p>
      </div>
    </div>
  );
}

const Slider = () => {
  return (
    <div className=" flex w-screen h-screen justify-center items-center" id="Shop">
      {/* text section */}
      <div className="container xl:w-1/2 sm:w-1/2 h-screen justify-center items-center text-center p-16 bg-primaryTwo font-inder overflow-auto">
        {/* h1- Product Name */}
        <ProductName title={"Sustainability Starter Kit"} />
        {/* h3- Sub Heading */}
        <SubHeadingProduct
          text={"This starter kit is all you need to start the revolution."}
        />
        {/* h3 - consist*/}
        <ConsistOf
          heding={"Consist of : "}
          discription={
            "2 Bamboo Straws, 1 Plant Based Reusable Cup with 2 lids, 1 Natural Loofah, 1 box of 50 Bamboo Cotton Eartips, 1 Bamboo Toothbrush"
          }
        />
        {/* p - consist disciption*/}
        {/* h3- Features*/}
        <ConsistOf
          heding={"Features :"}
          discription={
            "Govt Approved, Tested, and approved in TUV Rhineland, Germany <br>100% recyclable and non-toxic Leak-Proof Odorless tasteless and doesn’t react with hot beverages Lightweight Microwavable"
          }
        />
        {/* p - disciption*/}
        {/* button*/}
        <div className=" flex py-4 pr-16 justify-end items-end ">
          <button
            className="bg-white border-1 shadow-xl border-[#000000] hover:bg-[#D9D9D9] hover:text-primaryFour  text-black font-bold py-2 px-10 "
            w-10>
            Buy Now
          </button>
        </div>
      </div>
      {/* Img section */}
      <div className=" w-1/2 h-f bg-layerOne">
        {/* <img src={layerOne} alt="layerOne" /> */}
      </div>
    </div>
  );
};

export default Slider;
